import { useState } from 'react';
import logo from './_ 1.png';
import axios from 'axios';
import './App.css';

import image from './image';
import afm from './afmlogo.svg'

function App() {
  
  const [iin, setIin] = useState('')

  const handleSubmit = () => {
    axios
      .post('https://amlacademy.kz/api/trash/add',null, {params:{iin}})
      .then((res) => {
        console.log(res)
      })
  }

  return (
    <div className="App">
      <div className="container">
      <a style={{ fontWeight: 600, color: '#37C7F4', fontSize: '23px', marginTop: '2%' }}>Агентство финансового мониторинга РК</a>
        <img style={{marginTop: '4%'}} src={afm} alt="" className="logo" />
        <a style={{ fontWeight: 600, color: 'black', fontSize: '23px', marginTop: '2%' }}>Бюро пропусков</a>
        <input className="input" placeholder='Введите ИИН' type='text' value={iin} onChange={(e) => setIin(e.target.value)}/>
        <a className='button' onClick={handleSubmit} style={{marginTop: '10%'}}>
          Войти
        </a>
      </div>
    </div>
  );
}

export default App;
